mapper.select:
select id, manage_name as manageName,manage_type manageType from manage_type_info where is_delete <> 1
and manage_type=#{manageType}
order by create_time desc

mapper.insert:
replace into manage_type_info(
id,
manage_name,
manage_type

)
values (
#{id},
#{manageName},
#{manageType}


);
