mapper.select:
select
  id, manage_name as manageName,manage_detail manageDetail,
  manage_range as manageRange,
  remark,manage_type manageType,parent_id as pid
from manage_type_info
where is_delete  <>  1


    and manage_type=#{manageType}



    and manage_name=#{rationCode}



    and id=#{id}


order by create_time desc

mapper.insert:
replace into manage_type_info( id, manage_name, manage_detail, manage_type, manage_range, remark,
parent_id ) values ( #{id}, #{manageName}, #{manageDetail}, #{manageType}, #{manageRange}, #{remark}, #{pid}
);
