mapper.select:
select id,title,ration_id as rationId,fraction from ration_confidence_info
where ration_id = #{rationId}
order by create_time desc

mapper.insert:
replace into ration_confidence_info(
    id,
    title,
    fraction,
    ration_id
)
values (
#{id},
#{title},
#{fraction},
#{rationId}
);
